package LoopsAssignments;

public class Loops2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// A4: Write a program in Java to print "Hello World" ten times using while loop

		String s = "Hello World";

		int i = 0;
		while (i < 10) {
			System.out.println(s);
			i++;
		}

		// A5: Write a program in Java to print 1 to 10 using while loop
		int k = 1;
		while (k <= 10) {
			System.out.println(k);
			k++;
		}

	}

}
