package LoopsAssignments;

public class Loops4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// WAP to find out the max number from the given positive numbers

		int a = 10;
		int b = 20;
		int c = 500;

		if (a > b && a > c) {
			System.out.println("a is high " + a);
		} else if (b > a && b > c) {
			System.out.println("b is high " + b);
		} else {
			System.out.println("c is high " + c);
		}
	}

}
